INSERT INTO word (word_id, word, definition, sentence, level, news_id) VALUES
(1, '선입견', '사람이나 사물에 대해 미리 갖고 있는 고정된 관념이나 편견.', '많은 사람들은 새로운 것을 접하기 전에 선입견을 가지기 쉽습니다.', 'LOW', 1),
(2, '담임교사', '한 학교에서 학생들의 학급을 담당하여 지도, 보호하는 선생님.', '담임교사는 학생들이 학교생활을 잘 할 수 있도록 돕는 역할을 합니다.', 'LOW', 1),
(3, '심리상담', '마음의 고통이나 문제를 해결하기 위해 심리학적으로 상담을 받는 것.', '많은 사람들은 스트레스를 받을 때 심리상담을 받기도 합니다.', 'LOW', 1),
(4, '학폭', '학교 폭력의 줄임말로, 학교 내에서 학생들 사이에 발생하는 폭력을 지칭한다.', '최근 학폭 예방을 위한 다양한 프로그램이 도입되고 있다.', 'HIGH', 1),
(5, '보호자', '학생이나 미성년자를 돌보거나 책임을 맡고 있는 성인이나 부모.', '학생의 행동 문제에 대해 보호자는 교사와의 협력 관계를 유지하는 것이 중요하다.', 'HIGH', 1),
(6, '재발 방지', '어떤 문제가 다시 발생하는 것을 막기 위한 조치나 활동.', '학교에서는 폭력 사태의 재발 방지를 위해 관계 회복 프로그램을 운영하고 있다.', 'HIGH', 1),
(7, '부양비', '부양비는 자녀나 가족을 경제적으로 지원하는 비용을 말합니다.', '대학생 자녀에게 쓰는 월평균 부양비는 92만원에 육박한 것으로 조사됐다.', 'LOW', 5),
(8, '육박하다', '어떤 수치나 수준에 거의 다다르거나 근접하게 된다는 뜻입니다.', '대학생 자녀에게 쓰는 월평균 부양비는 92만원에 육박한 것으로 조사됐다.', 'LOW', 5),
(9, '진입', '어떤 장소나 상태에 들어가는 것을 의미합니다.', '보고서는 “성인 자녀의 노동시장 진입·안착의 지연 등으로 특정한 주거형태가 강요되고 가족 부담이 커지는 상황이 발생하지 않는지 면밀한 관찰이 이루어져야 한다”고 지적했다.', 'LOW', 5),
(10, '통상임금', '통상임금이란 근로자가 소정의 근로를 제공한 대가로 정기적이고 일률적이며 고정적으로 지급받는 임금으로, 연장∙야간∙휴일근로 수당 산정의 기준이 되는 임금을 말한다.', '대법원의 판결에 따라 조건부 정기상여금도 통상임금에 포함됨으로써 노사 간 갈등의 원인이 되고 있다.', 'HIGH', 2),
(11, '소급효', '법적 규정이 과거의 상태에 대해서도 효력을 가지는 것을 말한다. 주로 새로운 법령이나 판결 등이 이전 시점에 발생한 상황에 영향을 미칠 때 사용된다.', '대법원은 노사 혼란을 우려해 통상임금 판결의 소급효를 제한했지만, 관련 소송도 잇따르고 있다.', 'HIGH', 2),
(12, '준법투쟁', '법과 규정을 엄격히 지켜 업무 지연을 유도하는 쟁의행위로, 주로 노동조합이 투쟁의 일환으로 사용하는 방법이다.', '서울 시내버스노조는 준법투쟁에 돌입하여 사측과의 갈등을 표출하고 있다.', 'HIGH', 2),
(13, '갑질', '권력 또는 지위를 이용하여 아랫사람을 괴롭히거나 부당한 이득을 취하는 행위.', '그는 회사 내에서 자신의 위치를 이용해 지속적으로 갑질을 해왔다.', 'HIGH', 3),
(14, '교비회계', '학교에서 교육 활동에 필요한 자금을 관리하고 집행하는 회계 시스템.', '교비회계 집행 관련 부정으로 인해 몇몇 교직원들이 징계를 받았다.', 'HIGH', 3),
(15, '비위', '권력을 남용하여 저지르는 부정한 행위나 범죄.', '도교육청의 감사 결과 강원학원에서 다수의 비위 행위가 확인되었다.', 'HIGH', 3),
(16, '유예', '특정한 사항을 나중으로 미뤄 두거나 잠시 멈추는 것.', '경제 상황이 어려워지면서 많은 사람들이 세금 유예를 요청하고 있다.', 'LOW', 4),
(17, '권리행사방해', '다른 사람의 정당한 권리 행사를 방해하는 행위.', '그는 상대방의 계약 권리행사를 방해한 혐의로 재판에 넘겨졌다.', 'LOW', 4),
(18, '제적', '학교에서 학적을 취소하여 학생의 자격을 없앰.', '그 학생은 규정을 여러 번 어겨 결국 학교에서 제적당했다.', 'LOW', 4),
(19, '제적', '학생의 이름을 학교 등록부나 명단에서 지우는 것, 즉 학교에서 퇴출시키는 조치.', '대학교의 규정에 따라 잦은 결석이나 성적 미달로 인해 학생이 제적 처분을 받을 수 있다.', 'HIGH', 4),
(20, '성적경고', '학업 성적이 일정 수준에 미치지 못한 학생에게 경고를 주는 제도.', '성적경고를 받으면 별도의 학습 계획을 세워 성적을 향상시킬 필요가 있다.', 'HIGH', 4),
(21, '직권남용 권리행사방해죄', '공무원이 직권을 남용하여 사람으로 하여금 의무 없는 일을 하게 하거나 권리 행사를 방해하는 행위에 대한 죄.', '공무원이 자신의 권한을 초과하여 타인의 권리 행사를 방해할 경우 직권남용 권리행사방해죄로 처벌받을 수 있다.', 'HIGH', 4),
(22, '갑질', '사회적 신분이나 지위를 이용해 다른 사람에게 부당한 대우를 하는 행위.', '그 회사의 관리자들은 종종 직원들에게 갑질을 했다.', 'LOW', 3),
(23, '비위', '부도덕하고 불법적인 행위.', '회사의 비위 행위가 언론에 의해 폭로되었다.', 'LOW', 3),
(24, '징계', '잘못된 행동에 대해 벌을 주는 것.', '그 직원은 회사 규정을 위반하여 징계를 받았다.', 'LOW', 3),
(25, '판례', '법원에서 이미 행한 판결의 예. 다른 사건에서 법원의 판단에 영향을 줄 수 있는 판결이다.', '법원은 이전의 판례에 근거하여 새로운 사건을 심사할 수 있다.', 'LOW', 2),
(26, '통상임금', '근로자에게 매월 정기적이고 일률적으로 지급하는 임금으로 초과근로수당 등의 기준이 된다.', '통상임금을 기준으로 연장근로 수당을 계산할 때 변화가 생겼다.', 'LOW', 2),
(27, '소급효', '어떤 법이나 규칙이 지금부터 과거까지 영향을 미치는 것.', '대법원은 소급효 문제를 우려하여 판결의 적용 시점을 제한했다.', 'LOW', 2),
(28, '인구행동 심층 조사', '주로 인구의 행동과 관련된 요소들을 심층적으로 연구하기 위해 실시하는 조사. 이는 인구학적 특성, 사회적 행동, 소비 패턴 등 다양한 요소를 포함할 수 있다.', '이번 조사는 보건사회연구원이 3년 주기로 실시하는 인구행동 심층 조사의 일부다.', 'HIGH', 5),
(29, '노동시장 진입·안착', '노동시장에 새롭게 참여하거나 진입한 근로자가 안정적으로 직업에 적응하고 지속하는 과정.', '성인 자녀의 노동시장 진입·안착의 지연 등으로 특정한 주거형태가 강요되고 가족 부담이 커지는 상황이 발생하지 않는지 면밀한 관찰이 이루어져야 한다.', 'HIGH', 5),
(30, '가족과 출산 조사', '가족 구조, 출산 및 양육과 관련한 사회적 변화를 분석하기 위해 실시하는 조사. 이는 가족의 형태, 출산율, 양육비용 등에 대한 데이터를 포함한다.', '11일 한국보건사회연구원의 ‘2024년도 가족과 출산 조사’ 보고서에 따르면 자녀 유무나 혼인 상태와 관계없이 9639명을 대상으로 조사한 결과 51%는 ‘대학 졸업할 때까지’ 자녀를 경제적으로 지원해야 한다고 응답했다.', 'HIGH', 5),
(31, '중앙선거관리위원회', '중앙선거관리위원회는 대한민국의 중앙행정부에 속하는 행정위원회로, 선거가 공정하고 투명하게 진행될 수 있도록 관리하는 역할을 담당한다.', '김문수 후보는 중앙선거관리위원회에 정식 등록을 마치고 본격적인 선거운동을 시작했습니다.', 'HIGH', 6),
(32, '단일화', '정치에서 단일화는 각기 다른 후보가 합의하여 단일한 후보로 나서는 과정을 뜻하며, 주로 정치적인 효율성을 높이기 위해 이루어진다.', '김문수 후보는 한덕수 전 총리와의 후보 단일화 시도 과정에서 불거진 내홍을 수습하려 했습니다.', 'HIGH', 6),
(33, '비상대책위원회', '비상대책위원회는 위기 상황에서 문제를 해결하기 위한 특별한 의사결정 기구로, 특정 조직 내에서 빠르게 전략적 결정을 내리기 위해 구성된다.', '권영세 의원이 비상대책위원장 자리를 스스로 내려놓은 가운데, 김문수 후보는 대선 실무를 총괄할 인사를 내정했습니다.', 'HIGH', 6),
(34, '특검법', '특별검사법의 줄임말로, 특정 사건의 수사와 기소를 위해 특별히 임명된 검사에 관한 법률이다.', '이재명 후보는 ''채 상병 사건 진상규명을 위한 특검법을 반드시 통과시키겠다고'' 약속했다.', 'HIGH', 7),
(35, '준4군 체제', '현재 3군 체제(육군, 해군, 공군)에서 추가적으로 독립적인 체제로서 해병대를 설정하여 4개의 독립된 군 체계를 구성하는 것을 의미한다.', '이 후보는 해병대를 독립적인 ''준4군 체제''로 개편하고 해병대사령관의 위상을 격상하겠다고 밝혔다.', 'HIGH', 7),
(36, '상륙작전', '해군 및 해병대가 적의 해안으로 진입하여 지상 부대의 침투를 지원하거나 적의 영토를 점령하기 위해 수행하는 작전이다.', '이 후보는 해병대 임무를 상륙작전·신속대응 전담으로 특화하겠다고 말했다.', 'HIGH', 7),
(37, '진상규명', '어떤 사건이나 문제의 실제 상황이나 원인을 명확하게 밝힘.', '그들은 문제의 진상규명을 통해 사건에 대한 이해를 높였다.', 'LOW', 7),
(38, '외압', '외부에서 가해지는 압력이나 강요.', '조직 내 외압으로 인해 내부 갈등이 발생하며 문제가 커졌다.', 'LOW', 7),
(39, '계승', '이전 세대의 전통이나 가치, 문화를 이어받아 발전시킴.', '그들은 조상의 유산을 계승하여 전통 문화를 발전시켰다.', 'LOW', 7),
(40, '내홍', '내부에서 일어나는 갈등이나 다툼', '회사의 내홍을 해결하기 위해 많은 회의를 진행했다.', 'LOW', 6),
(41, '화합', '서로 맞추어 하나로 어우러짐, 또는 그렇게 만듦', '지역 사회의 화합을 위해 다양한 행사가 열렸다.', 'LOW', 6),
(42, '봉합', '갈등이나 상처를 마무리하거나 해결하기 위한 조치', '팀 내의 불화를 봉합하는 것이 그의 첫 번째 임무였다.', 'LOW', 6),
(43, '유세', '선거에 출마한 사람이 자신을 알리고 지지를 호소하기 위해 하는 활동.', '그는 대구를 찾아 첫 유세에 나섰다.', 'LOW', 10),
(44, '영입', '어떤 사람을 자기 편으로 끌어들임.', '이 지역 보수 진영의 전·현직 거물급 인사 영입 가능성을 언급한 것이다.', 'LOW', 10),
(45, '부상', '어떤 인물이나 세력 등이 주목받고 높은 위치로 나오게 됨.', '최근 이 후보는 보수·중도층의 폭넓은 지지를 받으며 대안 정치세력으로 부상하고 있다.', 'LOW', 10),
(46, '호소', '사람에게 어떤 일을 간절히 요청하거나 청하는 것.', '윤 전 대통령은 ''국민께 드리는 호소''라는 제목으로 글을 작성했다.', 'LOW', 9),
(47, '확장', '어떤 것을 늘려서 넓히거나 크게 하는 것.', '당 입장에서는 중도 확장을 가로막는 심각한 악재라고 했다.', 'LOW', 9),
(48, '엄숙하다', '말이나 행동이 조용하고 진지하여 격식을 차린 것처럼 보이는 상태.', '양 위원장은 ''계엄에 대해 국민에게 엄숙하게 사과해야 한다''고 강조했다.', 'LOW', 9),
(49, '가시화', '보이지 않던 것이 구체적으로 드러나 보임.', '김 후보 중심 당권재편 가시화', 'LOW', 8),
(50, '부추기다', '남을 이리저리 들쏘아 어떤 행동을 하도록 유도하거나 자극하다.', '권영세 비대위원장이 김 후보를 끌어내리기 위해 황우여 전 선관위원장을 부추겼다는 요지의 주장을 폈다.', 'LOW', 8),
(51, '선전포고', '전쟁을 시작하겠다는 공식 발표.', '김 후보는 8일 아침 긴급 기자회견을 열어 당무우선권을 발동하고 지도부에 “손 떼라”고 선전포고를 했다.', 'LOW', 8),
(52, '단일화', '두 개 이상의 대립되는 세력이나 의견을 하나로 통합하는 과정.', '김 후보가 전당대회에서 대선 후보로 선출될 때만 해도 단일화 과정이 이 정도로 격랑에 빠져들 거라 예상한 이는 많지 않았다.', 'HIGH', 8),
(53, '당무우선권', '정당 내에서 특정 일이 가장 중요한 사안으로 간주되어 우선적으로 처리되어야 한다는 권한.', '김 후보는 8일 아침 긴급 기자회견을 열어 당무우선권을 발동하고 지도부에 “손 떼라”고 선전포고를 했다.', 'HIGH', 8),
(54, '생중계', '일어난 사건이나 현장을 녹화하지 않고 즉시 전달하는 방송 방식.', '9일에는 의원총회에 참석한 김 후보와 지도부 간 갈등이 생중계되는 등 상황이 긴박하게 돌아갔다.', 'HIGH', 8),
(55, '유세', '정치적인 목적을 위해 여러 사람 앞에서 자신의 주장을 발표하고 지지를 호소하는 활동.', '후보자는 유세를 통해 자신의 공약과 정책을 설명하며 유권자들의 지지를 요청했다.', 'HIGH', 10),
(56, '재건축', '건축물이 노후화되었을 때 기존의 구조물을 철거하고 새로 건물을 짓는 과정.', '지역 사회는 오래된 건물을 재건축하여 새로운 주거 공간을 마련하려고 한다.', 'HIGH', 10),
(57, '입당', '특정 정당에 가입하여 당원으로 참여하게 되는 것.', '정치적 입장을 분명히 하기 위해 많은 시민들이 새로운 정당에 입당을 결정했다.', 'HIGH', 10),
(58, '전체주의', '하나의 정치체제에서 국가가 개인의 모든 삶을 통제하거나 지도할 것을 주장하는 정치 이념.', '윤 전 대통령은 김 후보의 정치가 거대 야당의 전체주의적 행태와는 근본적으로 다르다고 말했다.', 'HIGH', 9),
(59, '탄핵', '정부의 고위직 공무원이 잘못을 저질렀을 때, 그를 직위에서 해임하기 위한 정식 절차.', '한 전 대표는 김 후보가 탄핵 반대에 대해 진심으로 국민들께 사과해야 한다고 요청했다.', 'HIGH', 9),
(60, '계엄', '특정 상황에서 군대가 일정 지역의 질서를 유지하기 위해 행정과 사법권을 맡는 체제.', '양 위원장은 계엄에 대해 국민에게 엄숙하게 사과해야 이를 극복할 수 있다고 강조했다.', 'HIGH', 9),
(61, '거버넌스', '거버넌스는 단체나 조직의 관리 및 운영 체계 또는 구조를 의미하며, 주로 국가, 기업, 또는 기관의 의사 결정 과정의 투명성과 책임성을 강조하는 용어입니다.', '과학기술 거버넌스 체계 구축은 연구개발의 일관성과 효율성을 높이기 위해 중요하다.', 'HIGH', 13),
(62, '법제화', '법제화는 특정 사항이나 정책을 법률로 만드는 과정을 의미하며, 이는 사회적 합의와 정책의 일관성을 보장하기 위한 수단입니다.', 'R&D 예산 삭감사태 재발 방지를 위해 일정 비율을 보장하는 법제화가 요구되고 있다.', 'HIGH', 13),
(63, '지자체', '지방자치단체의 줄임말로, 지역 주민이 자치적으로 지역사회의 행정과 경제를 운영할 수 있도록 하는 지역 단위의 행정 조직을 의미합니다.', '이재명 후보는 지자체가 자율성을 가지고 R&D 투자 방향을 설정할 수 있도록 하는 방안을 제시했다.', 'HIGH', 13),
(64, '형평성', '사회적이고 경제적인 대우와 자원이 공정하고 균형적으로 분배되는 상태를 의미한다.', '학교별 개설 과목 수 차이로 인해 고교학점제의 형평성이 지적받고 있다.', 'HIGH', 12),
(65, '자율형사립고(자사고)', '정부의 규제보다 자율적으로 운영되는 사립 고등학교를 의미하며, 교육과정 운영에 있어 보다 높은 자유도를 가진다.', '서울지역 자율형사립고는 지역 일반고보다 개설 과목 수가 많아 학생들의 선택권이 넓다고 한다.', 'HIGH', 12),
(66, '고교학점제', '고등학생들이 다양한 과목 중 자신의 흥미와 적성에 맞는 과목을 자유롭게 선택하여 이수하는 교육 제도를 말한다.', '고교학점제는 학생들의 진로와 적성 탐색을 위해 여러 선택과목을 제공하지만, 학교별 과목 수 차이로 형평성 문제가 제기됐다.', 'HIGH', 12),
(67, '재고', '물건이 창고 등에 쌓여 있음. 흔히 아직 팔리지 않은 물건을 말함.', '한때 바닥을 드러냈던 유심 재고에 대해선 지난 9일부터 오는 18일까지 모두 117만 개가 추가 공급된다.', 'LOW', 11),
(68, '밝혔습니다', '어떤 사실이나 비밀 등을 사람들에게 알리다.', 'SK텔레콤은 일일브리핑에서 유심 무상교체 현황과 관련해 이같이 밝혔습니다.', 'LOW', 11),
(69, '대기자', '순서를 기다리는 사람.', '유심 교체를 신청하고도 받지 못한 대기자만 722만 명에 이르는 것으로 파악됐습니다.', 'LOW', 11),
(70, '유심(USIM)', '유심(USIM)은 가입자 식별 모듈로, 이동통신 사용자의 가입자 정보를 저장하는 작은 칩 형태의 카드입니다.', 'SK텔레콤이 유심(USIM) 정보 유출 사고를 계기로 이탈하는 이용자들에 대한 대처에 미온적인 태도를 보이고 있습니다.', 'HIGH', 15),
(71, '위약금', '위약금은 계약상의 의무를 위반했을 때 부과되는 벌금이나 손해배상금을 의미합니다.', '김송연씨는 위약금 때문에 통신사를 옮기는 것을 망설이고 있습니다.', 'HIGH', 15),
(72, '피싱', '피싱은 개인 정보를 빼내기 위해 신뢰할 수 있는 기관인 척하며 사용자에게 접근하는 사이버 사기 수법입니다.', '2차 피해 방지를 위해 피싱 사이트를 신속 차단하고 모니터링을 강화하고 있습니다.', 'HIGH', 15),
(73, '보이스피싱', '전화나 문자 메시지 등을 이용해 금융 사기를 저지르는 범죄 행위입니다.', '이처럼 최근 카드 배송원을 사칭한 보이스피싱 범죄가 잇따르고 있다.', 'HIGH', 14),
(74, '금융감독원', '대한민국의 금융 감독 기관으로, 금융시장과 금융기관을 감독하고 검사를 실시하는 기관입니다.', '이상하게 생각한 A씨가 카드배송원이 알려준 번호로 전화하자 상담원은 A씨 계좌가 범죄에 연루된 정황이 있다고 설명했다.', 'HIGH', 14),
(75, '범죄조직', '조직적인 체계를 갖추고 범죄 행위를 체계적으로 시행하는 집단입니다.', '특히 범죄조직의 수법이 다양해지며 지난해 강원지역 보이스피싱 발생건수는 584건, 피해금액은 254억원으로 집계됐다.', 'HIGH', 14),
(76, '공약화', '선거에 나오는 후보가 당선 후 실현하겠다고 국민에게 약속하는 일정한 사항을 정하는 것.', '우리는 조기 대선을 앞두고 후보자들에게 과학기술 정책 공약화 요구가 잇따르고 있다.', 'LOW', 13),
(77, '거버넌스', '조직의 목표를 달성하기 위한 의사 결정 및 관리 체계.', '개방적이고 독립적인 과학기술 거버넌스 체계 구축 등이 주요 내용이다.', 'LOW', 13),
(78, '노동조합', '근로자의 권익 보호와 근로 조건 개선을 위해 결성된 조직.', '노동조합과 연구자 단체 등이 대선 후보자들에게 과학기술정책 공약화를 요구하고 있다.', 'LOW', 13),
(79, '형평성', '형평성이란 차별이나 불공평이 없이 평등하고 공정한 상태나 특징을 말합니다.', '고교학점제 도입 초기부터 형평성에 대한 문제가 제기되었습니다.', 'LOW', 12),
(80, '자율형사립고', '자율형사립고란 일반 공립 학교와는 달리 학교 운영 및 교육과정에 있어 자율성을 부여받은 사립 고등학교를 말합니다.', '서울의 자율형사립고는 개설 과목 수가 다른 학교보다 많았습니다.', 'LOW', 12),
(81, '다양하다', '다양하다는 것은 여러 가지가 섞여 있거나 여러 가지의 성격, 종류, 형태 등이 있다는 뜻입니다.', '고교학점제는 학생들이 다양한 과목을 선택할 수 있도록 설계되었습니다.', 'LOW', 12),
(82, '유심', '유심(USIM)은 ''Universal Subscriber Identity Module''의 약자로, 이동통신 가입자의 신원 인증을 위한 칩이다.', '유심은 스마트폰에 삽입되어 네트워크 액세스를 가능하게 하는 작은 칩이다.', 'HIGH', 11),
(83, '재고', '재고는 기업의 생산, 유통, 판매 과정에서 보유하고 있는 상품이나 자재를 의미한다.', '한때 바닥을 드러냈던 유심 재고가 추가로 공급될 예정이다.', 'HIGH', 11),
(84, '무상교체', '무상교체는 어떠한 비용 없이 기존의 제품이나 부품을 새로운 것으로 교체하는 것을 의미한다.', '사용자는 특정 조건을 충족할 경우 유심을 무상으로 교체할 수 있다.', 'HIGH', 11),
(85, '미온적', '적극적이지 않고 열의나 활기가 없는 상태를 의미합니다.', '회사의 미온적인 태도가 소비자들의 불만을 초래했습니다.', 'LOW', 15),
(86, '귀책사유', '어떤 사건이나 문제에 대해 책임질 이유를 의미합니다.', '사용자의 귀책사유가 없을 경우 위약금이 면제됩니다.', 'LOW', 15),
(87, '신중하게', '조심스럽고 깊이 생각하여 행동하는 것을 의미합니다.', '이 문제를 해결하기 위해 신중하게 접근하고 있습니다.', 'LOW', 15),
(88, '계좌', '은행과 같은 금융 기관에 돈을 맡기고 인출할 수 있는 계약이나 약속.', 'A씨는 자신의 계좌가 범죄에 연루됐다는 얘기를 들었다.', 'LOW', 14),
(89, '이체', '어떤 계좌에서 다른 계좌로 돈을 보내는 것.', 'A씨는 거래은행으로 가서 10억원을 이체하였다.', 'LOW', 14),
(90, '범죄', '법률에 위반되는 행위나 행동.', '최근 카드 배송원을 사칭한 보이스피싱 범죄가 증가하고 있다.', 'LOW', 14),
(91, '자립', '다른 사람이나 기관의 도움 없이 스스로 생활하거나 일을 꾸려 나가는 것.', '우리는 어려운 상황에서도 자립하여 삶을 꾸려나갈 수 있어야 합니다.', 'LOW', 17),
(92, '제공', '무엇인가를 남에게 주거나 이용할 수 있게 해주는 것.', '회사는 직원들에게 다양한 복지 혜택을 제공하고 있습니다.', 'LOW', 17),
(93, '창출', '새로운 것을 처음으로 만들어 내거나 일으키는 것.', '정부는 경제 성장을 위해 새로운 일자리를 창출하려고 노력하고 있습니다.', 'LOW', 17),
(94, '합의', '두 사람 이상이 어떤 문제에 대해 서로 의견을 조정하여 결정함.', '이는 지난 8일 영국과의 무역 합의를 언급한 것으로, 이에 따르면 미국은 영국산 철강·알루미늄에 대한 25% 관세를 폐지하고...', 'LOW', 16),
(95, '폐지', '어떤 제도나 규칙 등을 없앰.', '이는 지난 8일 영국과의 무역 합의를 언급한 것으로, 이에 따르면 미국은 영국산 철강·알루미늄에 대한 25% 관세를 폐지하고...', 'LOW', 16),
(96, '면제', '책임이나 의무를 하지 않아도 되게 함.', '영국이 100억 달러 규모의 미국 보잉사 항공기 구매 조건으로 영국 롤스로이스사의 항공기 엔진 부품에 대한 관세를 면제하기로 했습니다.', 'LOW', 16),
(97, '자립', '다른 사람이나 것에 의지하지 않고 혼자서 살아감.', '그는 대학 졸업 후 자립할 수 있도록 열심히 준비했다.', 'LOW', 20),
(98, '형성', '무엇인가 만들어져서 이루어짐.', '청년 자산 형성 지원사업은 미래의 경제적 기반을 만들어주는 목표를 가진다.', 'LOW', 20),
(99, '만기', '계약이나 약속, 증서 등이 완료되는 시기.', '저축계좌에 돈을 넣고 3년 만기를 기다려 더 많은 적립금을 받았다.', 'LOW', 20),
(100, '실질 GDP', '실질 국내총생산(GDP)은 특정 기간 동안 한 나라 내에서 생산된 산출물의 가치를 물가 변동에 맞춰 실질적인 상승폭을 반영하여 계산한 값입니다.', '한국은행이 발표한 올해 1분기 우리나라 실질 GDP 성장률은 -0.2%로 추락했습니다.', 'HIGH', 19),
(101, '역성장', '경제에서 역성장이란 경제 규모가 작아지는 형상을 나타내는 용어로, 한 국가의 GDP가 이전 기간에 비해 감소하는 것을 의미합니다.', '역성장을 한 국가가 3나라였는데, 우리나라는 그중에서도 맨 꼴찌였습니다.', 'HIGH', 19),
(102, '추가경정예산', '추가경정예산이란 정부가 이미 확정된 예산 외에 경제 상황이나 정책적 필요에 따라 추가로 편성하는 예산을 의미합니다.', '새 정부가 출범하면 추가경정예산 편성이 불가피할 것으로 보입니다.', 'HIGH', 19),
(103, '지주사', '다른 회사를 지배할 목적으로 그 회사의 주식을 소유하는 회사로, 주로 사업 지배를 위한 수단으로 사용된다.', '한솔그룹의 지주사인 한솔홀딩스는 자회사 한솔피엔에스의 잔여주식에 대한 공개매수에 나섰다.', 'HIGH', 18),
(104, '공개매수', '특정 회사의 주식을 공개적으로 매수하여 지분을 확보하기 위한 기업 전략으로, 주식시장에서 다수의 주주로부터 주식을 매수하는 방식이다.', '한솔홀딩스는 한솔피엔에스 주식을 5월 12일부터 6월 22일까지 공개매수를 통해 사들일 예정이다.', 'HIGH', 18),
(105, '상장폐지', '상장된 회사가 자진하거나 강제로 주식시장에서 그 지위를 상실하게 되어, 주식이 더 이상 거래되지 않는 상태를 의미한다.', '한솔홀딩스는 한솔피엔에스를 상장폐지하여 자발적으로 주식시장에서 철수하려 한다.', 'HIGH', 18),
(106, '자활', '스스로의 힘으로 자신의 삶을 영위하고 나아가는 능력이나 과정.', '코레일은 자활의지가 높은 노숙인을 선발하여 일자리를 지원한다.', 'HIGH', 17),
(107, '공기업', '정부나 지방자치단체가 출자한 기업으로, 주로 공공서비스나 산업 정책적 목적을 위해 운영되는 기업.', '차성열 코레일 여객사업본부장은 공기업으로서의 사회적 책임을 강조했다.', 'HIGH', 17),
(108, '계열사', '하나의 기업군에 속한 여러 개의 기업 중 하나로, 일반적으로 모기업의 지배 하에 있는 상태를 의미.', '코레일 계열사에서도 프로그램 종료 후 취업이 가능한 경우가 있다.', 'HIGH', 17),
(109, '기준 중위소득', '기준 중위소득은 국민 가구의 소득 중심값을 뜻하며 사회 보험료, 세금 등을 제외한 소득을 기준으로 한다. 이는 복지 정책의 대상 선정과 지원 수준 판단에 사용된다.', '청년내일저축계좌는 기준 중위소득 50~100%에 해당하는 청년들을 대상으로 한다.', 'HIGH', 20),
(110, '저소득', '저소득은 한 개인이나 가구의 수입 수준이 어느 기준 이하인 상태를 의미하며, 일반적으로 최저 생활 수준을 유지하기 힘든 경우에 사용된다.', '청년내일저축계좌는 일하는 저소득 청년의 경제적 자립을 돕기 위한 지원사업이다.', 'HIGH', 20),
(111, '자산 형성 지원사업', '자산 형성 지원사업은 개인이나 가구가 향후 경제적 안정과 자립을 위한 자산을 축적할 수 있도록 국가가 직접적으로 지원하는 사업을 의미한다.', '청년내일저축계좌는 일하는 저소득 청년의 경제적 자립과 안정적인 자산 마련을 돕는 자산 형성 지원사업이다.', 'HIGH', 20),
(112, '관세', '수입품에 부과되는 세금으로, 특정 국가 간의 상품 교역을 조절하거나 보호하기 위해 사용된다.', '러트닉 장관은 기본 관세율이 10% 아래로 내려가지 않을 것이라고 말했다.', 'HIGH', 16),
(113, '무관세', '특정 상품의 수입에 대해 관세를 부과하지 않는 상태.', '일부 상품은 국가별 협상 결과로 무관세로 수입될 수 있다고 하였다.', 'HIGH', 16),
(114, '철강', '강철의 재료가 되는 금속으로, 주로 철광석을 제련하여 생산된다.', '미국은 영국산 철강에 대한 25% 관세를 폐지하기로 했다.', 'HIGH', 16),
(115, '추락', '높은 위치에서 낮은 위치로 떨어짐.', '비행기가 기체 문제로 급격히 추락하여 산에 충돌했다.', 'LOW', 19),
(116, '역성장', '경제 또는 특정 산업이 성장하지 않고 오히려 줄어듦.', '세계 경제 위기로 인해 여러 나라가 역성장을 겪고 있다.', 'LOW', 19),
(117, '불확실성', '앞날에 대한 확신이 없음. 미래의 상황이나 결과를 예측할 수 없는 상태.', '정치적 불확실성은 투자자들에게 더욱 신중함을 요구하게 된다.', 'LOW', 19),
(118, '계열사', '어떤 기업 그룹에 속한 회사로, 같은 그룹에 속하나 독립적으로 운영되는 회사', '한솔피엔에스는 한솔그룹의 시스템통합(SI) 계열사입니다.', 'LOW', 18),
(119, '주식', '회사가 자본을 나누어 파는 작은 지분 단위로, 이를 구매하면 그 회사의 일부 소유자가 됨', '한솔홀딩스는 한솔피엔에스 주식 326만290주에 대한 공개매수를 실시할 예정입니다.', 'LOW', 18),
(120, '인수합병(M&A)', '다른 회사의 경영을 지배하기 위해 그 회사의 주식을 사들이는 행위', '한솔피엔에스는 인수합병(M&A)으로 사업 다각화를 추진하고 있습니다.', 'LOW', 18),
(121, '사이토키닌', '사이토키닌(cytokinin)은 식물 조직에서 세포 분열과 성장을 촉진하는 호르몬의 일종으로, 특히 식물 생장에 중요한 역할을 한다.', '이들이 숙주를 키우기 위해 사용한 약물은 사이토키닌 계열의 합성 호르몬으로 원예나 조직 배양에는 쓸 수 있지만 장기 복용할 경우 부작용이 있을 수 있다.', 'HIGH', 25),
(122, '폐섬유화', '폐섬유화는 폐 조직이 비정상적으로 두꺼워지고 단단해져서 호흡 기능이 저하되는 만성적인 폐 질환이다.', '실제로 작년, 닥락성에서는 숙주를 먹은 35세 여성이 구토와 호흡 곤란 증세로 병원에 긴급 이송됐다가 이틀 뒤 폐섬유화 증세로 사망했습니다.', 'HIGH', 25),
(123, '선천성 기형', '선천성 기형은 태아가 모체 내에서부터 비정상적으로 형성되는 신체 구조의 기형을 의미하며, 유전적 요인이나 환경적 요인에 의해 발생할 수 있다.', '6-벤질아미노퓨린을 장기 복용할 경우 폐섬유증이나 선천성 기형, 저체중 출산 같은 부작용이 있어 식품 제조에 사용하는 건 금지되어 있습니다.', 'HIGH', 25),
(124, '별세', '사람이 세상을 떠나서 죽다.', '이옥선 할머니가 건강 악화로 별세했습니다.', 'LOW', 21),
(125, '참상', '비참하고 끔찍한 상황이나 상태.', '이 할머니는 일본군 위안부로 겪은 고초의 참상을 국내외에 알려왔습니다.', 'LOW', 21),
(126, '촉구', '어떤 일을 강하게 요구하거나 재촉하다.', '이 할머니는 일본의 공식 사죄를 촉구해왔습니다.', 'LOW', 21),
(127, '우선협상대상자', '특정 계약을 체결하기에 앞서 우선적으로 협상할 수 있는 자격을 가진 대상자.', '지난해 7월 한수원이 체코와의 우선협상대상자로 선정되자 관련 절차가 불공정했다며 체코 경쟁당국에 두 차례 이의를 제기했고, 이게 모두 기각되자 법원에 소송까지 냈다.', 'HIGH', 24),
(128, '기득권', '이미 기본적으로 가지고 있는 권리나 권력, 위치.', '프랑스가 이처럼 자신들의 수주 가능성을 아예 지워버리면서까지 소란을 일으키는 데는 이번 판을 뒤집으려는 것보다 유럽에서의 기득권을 과시하려는 의도가 있다는 게 원전 업계 관계자의 설명이다.', 'HIGH', 24),
(129, '신중함', '매우 조심하여 행동하는 성질이나 태도.', '현 정부에서 찾아보기 어려웠던 ‘신중함’의 가치는 되찾아야 한다.', 'HIGH', 24),
(130, '유체동산', '법률적으로 동산의 형태로 존재하여 물리적으로 이동할 수 있는 재산을 의미한다.', '불상을 훔친 일당은 형사처벌 받았고, 이와 별개로 유체동산인도 소송의 판결대로 정부는 일본 관음사에 불상을 인계해...', 'HIGH', 23),
(131, '약탈', '전쟁이나 강탈을 통해 다른 나라나 민족의 재산을 강제로 빼앗는 행위.', '불상이 고려 시대 왜구에 의해 약탈당해 일본으로 불법 반출됐을 상당한 개연성이 있다는 사정만으로...', 'HIGH', 23),
(132, '소급 적용', '법률이나 규칙이 제정되기 이전의 사건이나 상황에 그 법률이나 규칙을 적용하는 것.', '소급 적용되지 않고 강제력 없더라도 독일과 영국, 미국 등에서 문화유산의 취득 경위를 엄격히 따지고 있다.', 'HIGH', 23),
(133, '협상', '서로 다른 의견이나 입장을 조정하기 위해 토론하고 논의하는 과정.', '우리는 문제를 해결하기 위해 협상 테이블에 앉았다.', 'LOW', 26),
(134, '제시', '어떤 의견이나 방안 등을 상대에게 보여주거나 이야기하는 것.', '교사는 학생들에게 문제 해결 방법을 제시했다.', 'LOW', 26),
(135, '조율', '서로 다른 의견이나 입장을 맞춰 조화롭게 만드는 것.', '팀원들은 프로젝트 계획을 조율하기 위해 회의를 가졌다.', 'LOW', 26),
(136, '전승절', '전승절은 제2차 세계 대전 승리를 축하하는 기념일로, 전통적으로 주로 러시아와 일부 구소련 국가에서 국가적 행사로 기념된다.', '모스크바 붉은광장에서 전승절 기념 열병식이 열리고 있다.', 'HIGH', 22),
(137, '지상군', '지상군은 전장을 주로 지상에서 기동하는 군대의 한 부서를 의미하며, 보병, 기갑부대 등을 포함한다.', '우크라이나 전장에 지상군을 파병한 북한도 행사에 고위급 대표단을 파견했다.', 'HIGH', 22),
(138, '연쇄 회담', '연쇄 회담은 여러 주제를 연속적으로 다루는 구조의 회의를 의미하며, 통상적으로 국가 간의 복잡한 외교 문제를 해결하기 위해 활용된다.', '두 정상은 7시간 30분 동안 연쇄 회담을 갖고 협력 문서에 서명했다.', 'HIGH', 22),
(139, '비즈니스', '상업, 거래나 영리적 목적으로 행해지는 모든 활동을 일컫는 말이다.', '그는 새로운 비즈니스를 시작하기 위해 열심히 준비하고 있다.', 'LOW', 25),
(140, '호르몬', '생물체 내에서 특정한 기전을 통해 특정 기관이나 조직이 특정 방식으로 작용하도록 신호를 보내는 화학 물질이다.', '사춘기에는 여러 종류의 호르몬이 분비되어 신체에 큰 변화를 준다.', 'LOW', 25),
(141, '계열', '여러 개의 것들이 일정한 방향이나 성격을 갖추고 조화를 이루는 무리를 의미한다.', '언어 계열의 학문은 여러 나라의 문법과 어휘를 포함하고 있다.', 'LOW', 25),
(142, '위안부', '위안부는 일본 제국주의 시절, 일본군에 의해 강제로 성적 노역을 강요받은 여성들을 지칭하는 용어이다.', '이옥선 할머니는 일본군 위안부로 고초를 겪고 그 참상을 알리기 위해 노력해왔다.', 'HIGH', 21),
(143, '참상', '참상은 비참하고 처참한 상황이나 상태를 의미한다.', '수요시위를 통해 이옥선 할머니는 일본군 위안부 피해자들의 참상을 국내외에 알려왔다.', 'HIGH', 21),
(144, '공식 사죄', '공식 사죄는 국가나 조직의 대표가 공식적으로 자신의 책임을 인정하고 사과하는 행위를 의미한다.', '이옥선 할머니는 일본의 공식 사죄를 촉구하며 활동해왔다.', 'HIGH', 21),
(145, '발칵 뒤집혔다', '매우 놀라거나 큰 충격을 받아 큰 소란이 일어나는 상태가 되다.', '그 소식에 회사 전체가 발칵 뒤집혔다.', 'LOW', 24),
(146, '체결식', '월, 협정, 계약 등을 공식적으로 맺는 의식이나 행사.', '새로운 무역 협정 체결식이 다음 주에 예정되어 있다.', 'LOW', 24),
(147, '호락호락하지 않은', '쉽게 넘어가거나 부드럽게 다루어지지 않는, 완고한.', '그는 호락호락하지 않은 성격 덕분에 협상에서 항상 유리한 입장에 섰다.', 'LOW', 24),
(148, '반환', '다시 되돌려줌.', '우리는 이번 사건을 통해 약탈된 문화재의 반환 문제에 대해 많은 것을 배웠습니다.', 'LOW', 23),
(149, '증거', '어떤 사실이나 주장을 뒷받침하여 증명할 수 있는 근거가 되는 자료.', '약탈의 증거는 명확하였으나, 불상이 정상적으로 거래되었다는 증거는 없었습니다.', 'LOW', 23),
(150, '정당한', '논리와 이치에 맞아 올바른.', '문화재의 정당한 취득을 입증하지 못한 경우, 원래 국가에 반환하는 것이 국제적 추세입니다.', 'LOW', 23),
(151, '관세', '물품이 국가의 국경을 넘어갈 때 국가가 부과하는 세금으로, 주로 수입품에 부과된다.', '관세는 국가 간 무역을 조정하고 국내 산업을 보호하기 위해 사용된다.', 'HIGH', 26),
(152, '부과', '어떤 일에 대하여 세금이나 요금을 부담시키거나 지우는 것을 말한다.', '정부는 특정 품목에 대해 높은 세율을 부과하여 수익을 올린다.', 'HIGH', 26),
(153, '재조정', '원래의 계획이나 방식 등을 다시 조정하거나 수정하는 것을 의미한다.', '정부는 경제 정책의 방향을 재조정하여 시장의 불안정을 해소하려고 한다.', 'HIGH', 26),
(154, '파병', '군대가 외부의 전투나 전쟁에 참여하기 위해서 파견되어 가는 것을 말합니다.', '우크라이나 전장에 지상군을 파병한 북한도 이날 행사에 고위급 대표단을 파견했다.', 'LOW', 22),
(155, '참관', '어떤 중요한 행사나 상황을 직접 가서 봄으로써 살펴보는 것을 의미합니다.', '열병식을 참관하는 시진핑 중국 국가주석이 블라디미르 푸틴 러시아 대통령과 대화를 나누는 모습.', 'LOW', 22),
(156, '열병식', '군대가 정리된 상태로 장비와 함께 행진하여 힘을 과시하는 행사입니다.', '모스크바 붉은광장에서 열린 전승절 80주년 열병식에서 러시아군이 행진하고 있다.', 'LOW', 22);
INSERT INTO pnevaluation (pn_evaluation_id, positive_comment, negative_comment, news_id) VALUES
(1, '박진숙 교사의 지원과 대화로 학생들이 스스로 변화하는 모습은 훌륭하다. 교육활동보호센터의 다양한 지원은 교사들에게 큰 힘이 된다.', '가해 학생의 치료 및 재발 방지 제도가 부족해 보완이 필요하다. 공립형 병원학교의 부족함이 강원도 학교에 아쉬움을 남긴다.', 1),
(2, '성인 자녀에 대한 경제적 지원은 가족 간의 결속을 강화하며, 청년들이 보다 안정적으로 사회에 진입할 수 있도록 돕습니다.', '성인 자녀를 계속 부양하는 것은 부모에게 경제적 부담을 가중시킬 수 있으며, 자녀의 독립성 발달에 부정적인 영향을 미칠 수 있습니다.', 5),
(3, '의대 유급 제도는 학생들의 지속적인 학업 진보를 위해 필수적이며, 42.6%의 학생들이 유급 예정이라는 사실은 앞으로의 개선 가능성을 제시합니다.', '의대생들의 대대적인 유급은 교육부의 지침과 의대의 관리가 부실했음을 반영하며, 특히 갈등 해결 노력 없이 사태를 악화시키고 있다는 학생협회의 비판이 더욱 우려스럽습니다.', 4),
(4, '강원특별자치도교육청이 강원학원의 비위 행위를 투명하게 발각하여 조치한 것은 교육계의 신뢰 회복에 기여할 것이다.', '교육청의 과거 감독 부실로 인해 비위가 반복되었고, 이는 감독 기관의 책임을 명확히 지적받게 한다.', 3),
(5, '이번 판결은 근로자의 권리를 강화하는 긍정적인 변화로, 임금 투명성을 높이고 합당한 보상을 보장합니다.', '대법원 판결로 인해 노사 간 갈등이 심화되어 기업의 재정 부담이 증가하고 예기치 못한 쟁의가 발생할 우려가 큽니다.', 2),
(6, '이재명 후보의 해병대 독립성과 책임자 처벌 약속은 군의 위상 강화에 긍정적입니다.', '해병대사령관 위상 격상과 회관 건립은 정치적 이익과 지지율 확보를 위한 선심성 공약일 수 있습니다.', 7),
(7, '김문수 후보가 중앙선관위에 정식 등록하면서 공식적인 선거운동에 돌입한 점은 후보의 결연한 의지를 보여줍니다. ''원팀''과 ''광폭의 연대''를 강조하면서 당 내 결속을 다지고 단합을 통한 선거 승리를 목표로 하는 모습은 긍정적입니다. 이러한 리더십은 당원 간의 협력과 공감을 이끌어내어 선거에 대한 긍정적인 에너지를 불러일으킬 가능성이 있습니다.', '김문수 후보의 당내 단일화 실패와 내홍이 지속되면서 지도부 책임론이 언급되는 상황은 우려스럽습니다. ''빅텐트'' 전략을 강조했으나, 실제로 당내 갈등을 봉합하지 못할 경우 내부 분열을 초래할 수 있습니다. 또한 한덕수 전 총리와의 단일화 문제 해결을 위한 구체적 계획이 부족해 보이며, 의원들의 포용 여부가 미지수인 상황에서는 당내 대립 가능성이 여전히 잠재되어 있습니다.', 6),
(8, '윤석열 전 대통령의 적극적인 김문수 후보 지지는 당원들에게 일관된 방향성을 제공합니다. 이를 통해 자유민주주의를 강조하며 당의 결속을 도모할 수 있는 기회를 마련합니다.', '윤석열 전 대통령의 메시지는 중도 세력의 이탈 우려를 가중시키며, 선거에서 오히려 부정적인 요소로 작용할 수 있습니다. 일부 내부에서는 이로 인해 당명이나 선거 전략이 손상될 수 있다는 목소리가 나옵니다.', 9),
(9, '김문수 후보의 리더십과 당원들의 결속력이 주요한 역할을 하며 지도부의 교체 시도를 저지한 점은 당의 단결력을 보여줍니다.', '내부 갈등과 후보 교체 시도는 국민의힘이 조직적 통합과 신뢰를 구축하는 데 어려움을 겪고 있음을 시사합니다.', 8),
(10, '이준석 후보는 중앙선거관리위원회에 대선 후보 등록 후 대구 유세를 통해', '대구 유세에서 이준석 후보는 보수 유권자를 겨냥했으나, 국민의힘과', 10),
(11, '과학기술 정책에 대한 적극적인 관심과 지원은 국가 혁신과 발전에 큰 기여를 할 수 있으며, 연구 환경과 연구자의 처우 개선에도 긍정적 영향을 미칠 것입니다.', '과학기술 정책의 정치적 독립성을 강조하지만, 실제로 정책 집행 과정에서 정치적 압박이 여전히 존재할 수 있으며, 종합적인 지원과 개선이 부족할 수 있습니다.', 13),
(12, '고교학점제는 학생들에게 다양한 학습 기회를 제공하여 자신에게 맞는 학습 경로를 ''진로선택''과 ''융합선택''을 통해 찾아갈 수 있게 하는 긍정적 장점을 가지고 있습니다.', '학교 유형과 지역에 따라 ''선택과목'' 차별이 발생하고, ''서울'' 소재 학교와 ''일반고'', ''자사고'' 간 격차가 생기며 학생들이 불평등을 느끼게 할 우려가 있습니다.', 12),
(13, 'SK텔레콤이 2차 피해 방지를 위한 다각적인 노력을 기울이고 있으며, 고객 이탈을 심각하게 받아들이고 정확한 위약금 면제 결정을 위해 심사숙고하고 있다.', 'SK텔레콤의 미온적인 태도와 위약금 면제 결정 지연에 대한 소비자들의 불만이 커지고 있으며, 문제 해결을 위한 신속한 대응이 절실하다.', 15),
(14, '강원 지역 기관들이 보이스피싱 예방에 적극적으로 나서는 모습은 고무적입니다. 이를 통해 주민들은 피해를 예방할 수 있고, 금융 안전에 기여할 수 있을 것입니다.', '보이스피싱 범죄 수법이 날로 다양해지고 피해 규모가 커지는 것은 심각한 문제입니다. 이러한 범죄를 줄이기 위한 제도적 뒷받침이 절실히 필요합니다.', 14),
(15, 'SK텔레콤의 신속한 대응으로 유심 재고 확보와 추가 공급을 통해 빠른 문제 해결을 위한 노력이 돋보이며, 많은 가입자들이 이미 교체 서비스 혜택을 누리고 있는 점이 긍정적입니다.', '신청 후 유심을 받지 못한 대기자 수가 여전히 많아 가입자들 간의 불안감이 고조될 수 있으며, 초기 재고 부족 사태로 인한 혼란이 기업 대비 부족함을 드러내고 있습니다.', 11),
(16, '어려운 시기지만 새 정부 출범으로 정책 변화 가능성을 통해 경제 회복 기회가 있을 것입니다.', '현재 경제성장률은 최하위이며, 정치적 혼란이 장기화되면서 경제적 불확실성이 가중되고 있습니다.', 19),
(17, '한솔홀딩스의 한솔피엔에스 공개매수는 회사 장기적 비전 강화 및 자회사 경쟁력 높이는 데 기여. 공개매수 진행으로 투자자 보호 기회 제공하며 긍정적 이미지 구축.', '상장폐지 시 투자자 유동성 감소, 정보 접근 어려움 가능성 존재. 높은 지분 확보 위한 추가 비용 발생은 시장 내 불안정성 초래 가능성 내재.', 18),
(18, '코레일의 노숙인 일자리 제공 사업은 사회적 책임을', '월 60시간의 근로가 실질적 자립에 충분하지 않을 수', 17),
(19, '국가별로 유연하게 접근하여 합리적 무역 거래를 촉진하는 것은 대단한 결정입니다. 특히 철강, 알루미늄, 항공기 부품의 무관세 협상은 미국과 영국 양국의 경제적 이득을 극대화할 수 있는 기회를 제공합니다.', '기본 관세가 10% 이하로 내려가지 않는 것은 무역 상대국들에게 경제적 부담을 지속시킬 가능성이 있습니다. 이러한 고정 관세 정책은 국제 무역 관계를 조화롭게 하기보다는 장애 요소로 작용할 수 있습니다.', 16),
(20, '정부지원금을 통해 청년들이 더 큰 자산을 형성할 수 있는 기회를 제공합니다. 저소득 청년을 위한 파격적인 지원은 경제적 자립과 미래를 계획하는 데 큰 도움이 됩니다. 월 10만 원의 정부 지원과 소득 상한선 완화는 많은 청년들에게 창의적 미래를 열어줄 것입니다.', '꾸준한 근로활동과 자립 역량 교육 등 다양한 요건을 충족해야 지원금을 받을 수 있어 접근이 어려울 수 있습니다. 소득 상한선을 고려하더라도 일부 청년들은 지원 요건에 맞지 않을 수 있습니다. 단순히 금융 지원뿐만 아니라 취업 기회 확대 등 복합적 지원이 필요할 수 있습니다.', 20),
(21, '베트남 정부가 가짜 식품을 엄격히 단속하고 있어요. 불법 행위에 대해 강력히 대응하는 모습은 앞으로도 믿을 수 있는 식료품을 소비자들에게 제공합니다. 베트남의 건강한 식생활이 복원될 것으로 기대합니다.', '베트남 식품 산업에 신뢰 문제가 제기되며 소비자들의 불안이 커지고 있습니다. 약품 사용 등 불법 행위는 소비자 건강에 큰 위협이 되며, 이런 사태가 반복될 경우 베트남의 식품 시장에서 신뢰가 무너질 수 있습니다.', 25),
(22, '한국의 지속적 노력과 정치적 협력이 돋보입니다.', '프랑스의 방해로 인해 계약 지연이 우려됩니다.', 24),
(23, '이번 반환은 문화유산의 올바른 소유 확인을 촉진하고 국제적 약탈 유물 반환 추세에 부합하는 긍정적인 조치로 평가받을 수 있습니다.', '하지만 일본의 역사적 책임과 약탈에 대한 깊이 있는 반성과 수용이 부족한 상태로, 문화재 반환은 진정한 해결책이 아닐 수 있습니다.', 23),
(24, '전승절 80주년을 기념하며, 러시아와 중국의 정상들이 한자리에 모여 의미 있는 협력의 장을 마련했다는 점은 매우 뜻깊다.', '북한의 고위급 대표단 파견과 우크라이나 전장에 지상군 파병은 국제 사회에 긴장감을 불러일으킬 수 있어 우려된다.', 22),
(25, '이옥선 할머니는 자신의 아픔을 딛고 일본군 위안부 피해자의 증언자로 활발히 활동하며, 일본의 사죄를 끌어내기 위해 끊임없이 노력하셨습니다. 그분의 헌신이 오늘날 많은 이들에게 역사적 교훈을 전하고 있으며, 공정한 역사를 세우는 데 큰 기여를 했습니다.', '슬프게도, 이옥선 할머니의 별세로 인해 일본군 위안부 피해자의 수가 점점 더 줄어들고 있습니다. 일본의 공식 사죄가 이루어지지 않은 상황에서 피해자분들이 대부분 떠나시는 현실이 안타깝게 느껴집니다. 이로 인해 피해자들의 목소리가 더욱 약화될 위험이 커지고 있습니다.', 21),
(26, '미국과 중국의 협상은 글로벌 경제 불안을 완화하고 무역 증진 기회를 높입니다.', '속도전 협상은 신중하지 못할 수 있어, 장기적 피해 우려가 있습니다.', 26);
INSERT INTO summary (summary_id, level, summary_content, news_id) VALUES
(1, 'Low', '국민의 88%가 성인이 된 자녀를 경제적으로 지원해야 한다고 여긴다. 보사연의 보고서에 따르면 응답자 중 51%는 대학 졸업까지, 28.7%는 취업할 때까지 지원이 필요하다고 답했다. 대학생 자녀의 월평균 부양비는 92만원에 달하며, 이는 중·고등학생보다 적고 초등학생보다 많다. 용돈 및 교통·통신비, 공교육비 등이 주를 이룬다. 30세 이상 성인의 부모와의 동거 비율도 증가 추세에 있다.', 5),
(2, 'Low', '교육부는 전국 40개 의과대학의 자료를 바탕으로, 유급 예정 학생 8,305명 중 42.6%를 확정 발표했다. 특히 1개 과목만 수강 신청한 1,389명도 유급 대상에 포함되었으며, 강원대 의대생 대다수가 이 방식으로 수강해 7월 대거 유급될 수 있다. 한편, 대한의과대학·의학전문대학원학생협회는 오석환 차관 등을 고위공직자범죄수사처에 직권남용 권리행사방해죄 및 강요죄로 고발했다.', 4),
(3, 'Low', '강원학원의 비리 의혹이 드러나 교육청이 조사한 결과, 많은 교직원들이 책임을 물게 되었습니다. 도교육청은 이사장이 금품을 받은 사실을 확인하고, 여러 교직원에게 형사 고발과 징계를 내릴 예정입니다. 도교육청은 시설 관리 부실이나 교비 사용 문제도 확인했고, 관련자들에 대한 추가 조사와 징계를 요청할 계획입니다. 하지만 교직원 노동조합은 교육청이 그동안의 문제를 알지 못했다는 입장을 비판하고 있습니다.', 3),
(4, 'Low', '지난 12월 대법원 판결로 조건부 정기상여금이 통상임금에 포함된다는 판례가 나오며 전국적 노사 갈등이 확산되고 있다. 서울시 버스노조는 통상임금 증가와 기본급 인상을 요구하는 반면, 사측은 기존 임금체계 개편을 주장하고 있다. 기업들은 고용노동부에 해석과 가이드라인을 문의하고 있다. 법무법인 태평양의 김상민 변호사는 수당 정리와 성과 기반 상여금으로의 전환을 제안하지만, 노사 합의가 필요하여 갈등이 장기화될 것으로 보인다.', 2),
(5, 'Low', '박진숙은 담임교사로서 학급 내 학폭을 예방하기 위해 노력했습니다. 학폭을 일으킨 학생은 처음에는 반발했지만, 다른 학생들이 담임교사를 도와주면서 상황이 좋아졌습니다. 심리상담 등 교육당국의 지원이 큰 도움이 되었습니다. 그러나 가해 학생에 대한 재발방지 제도가 부족하여, 병원학교와 사회봉사 등으로 보완이 필요합니다.', 1),
(6, 'High', '강원특별자치도교육청은 강원학원의 비위행위를 적발해 전 이사장 및 교직원을 징계 및 고발할 예정입니다. 전 이사장은 일부 교직원에게 금품을 받은 혐의로 조사받고 있으며 관리책임 관계자 5명에게 징계를 요청했습니다. 시설 분야 부실로 11명에 대해 중징계 및 경찰 수사를 의뢰했고, 총 1억6,249만8,000원의 예산 회수를 명령했습니다. 교비회계 부정으로도 여러 명이 징계 대상입니다. 전국교직원노동조합 강원지부는 도교육청의 감독 책임 부족을 지적했습니다.', 3),
(7, 'High', '전국 의대생 42.6% 유급 예정, 교육부는 8,305명 발표, 강원대 대다수 유급 가능성. 대한의과대학학생협회는 교육부를 직권남용 혐의로 고발.', 4),
(8, 'High', '박진숙 교사는 학급 내 학교폭력 문제를 다루며 어려움을 겪었다. 담임교사로서 가해 학생과의 반복 상담에도 불구하고 보호자와 학생의 선입견이 존재했다. 아이들과의 소통을 통해 조금씩 변화가 있었고, 피해 학생이 거절의사를 표명했다. ''교육활동보호센터''는 법률 및 심리상담 등을 지원하며 교사들에게 큰 위로가 된다. 그러나 가해 학생 재발방지 및 강원도 병원학교 설립 필요성을 강조하며, 사회봉사나 특별교육의 체계적 운영이 필요하다고 밝혔다.', 1),
(9, 'High', '대법원의 ''조건부 정기상여금도 통상임금'' 판례로 서울시 버스노조는 통상임금 증액과 기본급 인상을 요구하며 사측과 갈등을 빚고 있다. 사측은 기존 임금체계에 맞춰 통상임금 인상분 조정 필요성을 주장한다. 28일 총파업 가능성이 있는 가운데, 다른 기업들도 임금 협상에서 통상임금을 주요 쟁점으로 삼고 있다. 고용노동부에는 여러 기업으로부터 통상임금 관련 질의가 증가하고 있으며, 일부 노조는 소송을 제기하고 있다. 전문가들은 향후 노사갈등이 지속될 것으로 전망한다.', 2),
(10, 'High', '2024년 보건사회연구원 조사에 따르면, 국민 88%가 자녀가 성인이 된 이후에도 경제적 지원이 필요하다고 인식했다. ‘취업 시점까지’ 지원 응답은 증가 추세다. 대학생 자녀 월평균 부양비는 약 91.7만 원이며, 주요 항목은 용돈·교통비와 등록금이다. 30대 성인의 부모 동거율도 증가해 가족의 경제·주거 부담이 커지고 있다.', 5),
(11, 'High', '국민의힘 김문수 대선 후보가 선관위에 기호 2번으로 등록하여 선거운동을 시작했습니다. 후보 단일화 문제로 당 내홍이 있었지만, ''원팀''을 강조했습니다. 김 후보는 한덕수 전 총리와의 단일화 실패에 대해 내홍을 수습하며, 광폭의 ''빅텐트'' 전략을 제시하며 지도부 물갈이 가능성을 일축했습니다. 권영세 의원이 비대위직 사퇴했고, 박대출 의원이 사무총장에 내정되었습니다. 의원들의 협조가 이뤄질지는 미지수입니다.', 6),
(12, 'Low', '국민의힘에서 윤석열 전 대통령의 메시지가 비판을 받고 있습니다. 윤 전 대통령은 페이스북에 김문수 후보 지지를 선언하며 자유민주주의를 강조했습니다. 그러나 양향자와 한동훈 등 당내 인사들은 중도 확장을 막고 있어 부정적이라고 지적했습니다. 윤 전 대통령의 제명이나 출당 요구까지 나왔으며, 윤재옥 본부장은 지나간 일보다는 미래 비전을 강조하겠다고 했습니다.', 9),
(13, 'High', '이준석 개혁신당 후보가 대구에서 첫 유세를 했습니다. 그는 대구 시민들이 변화의 중심이 될 수 있다고 믿고 지지를 요청했습니다. 그에 대한 관심이 높아지고 있으며, 온라인 커뮤니티에서도 많은 사람들이 참여하고 있습니다. 개혁신당의 온라인 당원 수는 8만 명 이상으로 증가했으며, 유튜브 채널도 빠르게 성장 중입니다. 정치인 홍준표는 이준석을 주목하며 그가 새로운 정치세력으로 자리 잡을 가능성이 있다고 언급했습니다.', 10),
(14, 'Low', '김문수 후보는 국민의힘 지도부의 후보 교체 시도를 당원들의 반대로 저지했습니다. 지도부는 단일화를 압박했지만 김 후보는 지도부에 반발하며 당무우선권을 주장했습니다. 권성동 원내대표는 단식을 통해 압박을 가했지만 김 후보는 결국 당내 지지를 받아 당권 재편을 이루었습니다.', 8),
(15, 'High', '이재명 후보는 ''채 상병 사건''의 특검법 통과를 약속했습니다. 해병대의 명예를 세우고 박정훈 대령이 자리로 돌아가도록 하겠다고 했습니다. 외압을 밝히고 책임자를 처벌하겠다고 했고, 해병대를 새로운 체제로 개편하고, 임무를 상륙작전과 신속대응으로 특화하겠다고 했습니다. 또한, 해병대 독립 회관을 건립해 자긍심을 높이겠다고 약속했습니다.', 7),
(16, 'Low', '개혁신당의 이준석 후보가 대선 후보 등록 후 대구를 방문하여 첫 선거운동을 시작했습니다. 그는 대구에서 70~80%의 지지를 받아야 변화가 일어난다며 지지를 요청했습니다. 이 후보는 대구에서 여러 행사에 참여하며 중요 인사들과 대화하고 있다고 했습니다. 국민의힘의 비정상적인 후보 교체에 실망한 유권자들로 인해 개혁신당의 지지세가 증가하고 있으며, 당원 수가 8만9100명을 넘었습니다. 이준석 TV도 성장하고 있습니다. 정치권에서는 그의 주목도가 높아졌습니다.', 10),
(17, 'Low', '이재명 더불어민주당 대선 후보는 페이스북을 통해 ''채 상병 사건'' 진상규명을 위한 특검법을 통과시키겠다고 약속하며, 불법 권력에 맞서 수사를 수행한 박정훈 대령이 제자리로 돌아갈 수 있게 지원하겠다고 밝혔다. 해병대의 위상을 격상하기 위해 ''준4군 체제''로 개편하고, 해병대 사령관 위상을 높이며, 상륙작전 및 신속대응 임무를 특화시키겠다고 덧붙였다. 또한 해병대 독립 회관 건립을 통해 전우애를 기리고 자긍심을 강화하겠다고 선언했다.', 7),
(18, 'High', '국민의힘 김문수 후보는 단일화 압박에도 불구, 지도부 교체 시도를 극복하며 당권 재편에 성공했습니다. 김 후보는 전당대회 후 단일화 압박에 맞서 당무우선권을 발동하며 지도부에 반발했습니다. 권성동 원내대표는 단식으로 반대 의사를 표명했고, 김 후보와 지도부 간 갈등은 의원총회까지 이어졌습니다. 그러나 새벽 후보등록 제한이 당원들의 반발을 불러일으켜 후보 교체는 무산되었습니다. 이로 인해 김 후보 중심으로 당권 재편이 가시화되었습니다.', 8),
(19, 'High', '국민의힘에서 윤석열 전 대통령의 김문수 후보 지지 발언에 대해 비판과 우려가 나왔다. 양향자 선대위원장은 이 발언이 중도 확장을 막는 악재라고 했고, 한동훈 전 대표는 출당 필요성을 주장했다. 조경태 의원은 강도 높게 반발했다. 윤재옥 총괄본부장은 과거에 얽매이지 않고 미래 비전을 제시하겠다고 밝혔다.', 9),
(20, 'Low', '김문수 국민의힘 대선 후보가 중앙선관위에 후보 등록을 완료, 선거운동에 돌입했습니다. 당 내홍을 의식한 김 후보는 ''광폭의 연대''와 ''원팀''을 강조하며 당내 갈등 봉합에 나섰습니다. 한덕수 전 총리와의 단일화 시도 후폭풍 속에서 김 후보는 당내 질서를 정비하고 대통합을 시도, 선대위원장 자리를 제안했으나 한 전 총리는 즉답을 피했습니다. 현 지도부 책임론이 부각되는 가운데, 권영세 의원의 비상대책위원장 사퇴, 박대출 의원의 사무총장 임명 등이 진행되며 의원들의 협조가 주목됩니다.', 6),
(21, 'High', 'SK텔레콤 해킹 이후 유심 교체 가입자는 143만. 대기자 722만명. 유심 재고는 117만 개 18일까지 추가 공급. 다음 달 말까지 1077만 개 순차 확보 예정. 유심 교체 문자를 받은 가입자는 T월드 매장에서 ''유심 재설정''과 ''유심 교체'' 선택 가능, 재설정 후 1회 무상 교체 가능.', 11),
(22, 'Low', 'SK텔레콤 해킹 사건 후 143만 가입자가 유심을 교체했으며, 722만 대기자가 있습니다. 117만 개의 유심이 추가 공급됩니다. 다음 달까지 1,077만 유심이 확보됩니다. 유심 교체 가능 문자를 받은 가입자는 T월드 매장에서 ''유심 재설정''이나 ''유심 교체''를 선택할 수 있습니다. ''유심 재설정'' 선택 시, 유심 무상 교체가 한 번 가능하다고 합니다.', 11),
(23, 'Low', '조기 대선을 앞두고 후보자들에게 과학기술 정책을 공약으로 요구하는 움직임이 늘고 있습니다. 연구개발(R&D) 예산 삭감 방지를 위한 법적 보장과 개방적 과학기술 관리 체계 구축이 주요 요구사항입니다. ''출연연과학기술인협의회총연합회(연총)''는 대선 후보자들에게 독립적인 과학기술 정책 수립을 요청했습니다. 연구원들은 현재 여러 부처에 나뉘어 있는 R&D 과제들을 협력적으로 조정하고 지원할 것을 제안했습니다. 과학기술 예산 5% 이상 보장을 위한 법제화와 연구자들의 지위가 향상되길 기대하며, 연구 현장의 철회 요구인 연구과제중심제도(PBS) 폐지도 주장되었습니다. 대선 후보들은 과학기술 공약을 통해 연구자들에게 다가가고 있습니다.', 13),
(24, 'High', '최근 강원도에 사는 50대 A씨는 신청하지 않은 카드가 배달된다는 문자를 받고 피싱 사기에 당했습니다. 이처럼 보이스피싱이 늘고 있으며, 작년 강원지역에서는 584건, 254억원이 피해로 나타났습니다. 이에 경찰과 국정원, 금융기관들이 예방에 나섰고, 농협·국민·신한은행은 ''스피싱 체크리스트''를 대기표에 넣어 최신 피싱 정보를 제공합니다.', 14),
(25, 'High', 'SK텔레콤이 USIM 정보 유출로 인한 사용자 이탈을 막기 위해 위약금 면제를 두고 어려움을 겪고 있습니다. SKT는 최대 500만 가입자 이탈과 7조원의 손실을 예상하고 있으며, 위약금 면제를 시도하는 이용자들이 늘고 있는 상황입니다. 유영상 SKT 대표는 결론을 아직 내리지 못했고, 과기정통부 유상임 장관은 신중한 접근을 강조했습니다.', 15),
(26, 'Low', '고교학점제가 도입된 올해, 서울 및 자사고는 많은 과목을 제공하지만, 지역 일반고는 적은 과목으로 학생 선택에 차별이 발생한다는 지적이 있다. 종로학원의 분석에 따르면 전국 41개 고교 중 자사고 A학교가 127개 과목으로 가장 많으며, 지역 소규모 일반고인 B학교는 63개로 가장 적다. 특히 진로선택과 융합선택 과목 비중이 커지고 있으며, 내신 등급제에 따라 전략적인 과목 선택이 필요하다.', 12),
(27, 'Low', 'SK텔레콤의 유심 정보 유출로 인한 이용자 이탈과 관련하여 위약금 면제 문제를 두고 논란이 이어지고 있습니다. SKT의 약관에 따르면 귀책사유가 있을 경우 위약금이 면제되지만, 유영상 SKT 대표는 현재 논의 중이며 결정의 어려움을 표명했습니다. 유상임 과학기술정보통신부 장관도 신중한 접근을 강조하며 6월 말 결정을 예고했습니다. SKT는 최대 500만 가입자 이탈과 3년간 7조원 손실을 우려하고 있으며, 2차 피해 방지를 위한 조치를 진행 중입니다.', 15),
(28, 'High', '올해 전국 고등학교에 도입된 고교학점제는 학교별로 개설 과목 수가 최대 2배 차이를 보이며 형평성 문제를 일으키고 있다. 서울 및 자사고에서는 과목 수가 많고, 지역 일반고에서는 적어 학생 선택권에 불평등이 발생한다. 종로학원 조사에 따르면, 서울 자사고의 평균 개설 과목 수는 100개 이상이지만 지역 일반고는 75.6개에 그친다. 선택과목은 일반, 진로, 융합으로 나뉘며, 과목 선택은 대입 전략에 큰 영향을 끼친다.', 12),
(29, 'High', '조기 대선을 앞두고 후보자들에게 과학기술 정책 공약화 요구가 잇따르고 있다. 연구개발(R&D) 예산 삭감사태 재발 방지를 위한 일정 비율 보장 법제화와 개방적이고 독립적인 과학기술 거버넌스 체계 구축 등이 주요 내용이다. 11일 과학기술계에 따르면 노동조합과 연구자 단체 등이 6월 3일 대선 후보자들에게 과학기술정책 공약화를 요구하고 있다. 4월 과학의 달과 조기 대선 시점이 맞물리면서 과학기술계 기대와 요구가 더 확산되는 모양새다. 23개 과학기술 정부 출연연구기관(출연연) 연구자 2600여명이 소속된 (사)출연연과학기술인협의회총연합회(이하 연총)는 9일 21대 대선공약 요청 제안서를 통해 출연연 혁신 정책을 후보자들에게 주문했다. 연총은 과학기술 정책 수립과 실행을 독립적으로 수행할 수 있도록 기획과 예산권이 보장된 혁신적 상위 행정기구 신설을 요구했다. 또 현재 각 부처에 분산돼 있는 R&D 과제와 역할을 개방적 협업·협력으로 지원하고 조정할 것을 제안했다. 과학기술 R&D 정책과 평가기관의 정치 독립성 보장을 위해 기관장 임명이나 재임명 평가 시 구성원 의견 반영 요구와 함께 과학기술 R&D 예산 5% 이상 법제화를 통해 국제 협력과 지역 발전을 위한 과학기술 R&D 예산 지속 확보 요구, 출연연 위상 재정립을 위한 정년 환원, 임금피크제 폐지, 연구 중심의 선진적 행정 지원체계 마련 정책화도 주문했다. 그동안 꾸준히 제기된 연구과제중심제도(PBS) 폐지도 요구했다. 앞서 공공과학기술연구노동조합(과기연구노조)은 과학의 날을 앞둔 4월 17일 R&D 예산 복원과 안정적 지원을 위한 법제화, 국가 과학기술 시스템 개혁, 출연연과 4대 과학기술원 등 공공연구기관의 공공성·자율성 확대, 공공연구기관 종사자 사기진작과 처우개선 등을 정책 요구한 바 있다. 대선 후보들은 연구 현장을 방문하거나 공약을 내놓으며 연구 현장 표심을 노리고 있다. 이재명 더불어민주당 후보는 앞서 4월 21일 과학의 날을 맞아 공개한 과학기술 공약을 통해 R&D 예산 확대, 과학기술인 존중 및 처우개선, 연구·교육 활성화를 위한 지역 기반 강화, 지자체가 자율성 갖고 R&D 투자 방향 설정 등을 약속했다. 김문수 국민의 힘 후보는 과학기술부총리와 과학특임대사 신설을 비롯해 과학기술인 처우 개선과 위상 강화, 세계적 수준의 연구 중심 허브 육성·연구 몰입 환경 조성 등을 공약했다. 연구현장이 지속적으로 요구한 정년 환원, PBS 폐지 또는 전면 개선도 공약으로 내걸었다.', 13),
(30, 'Low', '강원도 A씨가 신청하지 않았던 카드가 집으로 배송된다는 문자를 받고, 보이스피싱 범죄에 당해 10억원을 이체했다. 작년 강원지역의 보이스피싱 발생 건수는 584건, 피해금액은 254억 원이었다. 강원경찰청과 국정원, 금융기관들은 보이스피싱을 막기 위해 힘쓰고 있으며, 농협·국민·신한은행에서는 대기표에 피싱 예방 문구를 추가하고 있다. 경찰, 국정원, 금융기관 관계자들은 전국 확산 계획을 언급했다.', 14),
(31, 'Low', '코레일은 2025년까지 전국 기차역 노숙인 104명에게 일자리를 제공합니다. 서울 18명 등 다양한 지역에서 모집하며, 노숙인들은 환경미화 등의 일을 합니다. 코레일과 지자체, 노숙인지원센터가 협력하여 주거비 지원과 자활 프로그램을 운영하며, 이를 통해 노숙인의 사회 복귀를 돕습니다.', 17),
(32, 'Low', '하워드 러트닉 미 상무장관은 트럼프 대통령의 10% 기본 관세를 유지하겠지만, 국가별로 특정 품목에 대해 무관세를 적용할 가능성을 언급했습니다. 러트닉 장관은 국가별 협상에서 유연성을 강조하며, 영국과의 합의를 통해 철강, 알루미늄 등에서 25% 관세를 폐지하고, 항공기 관련 부품에 무관세를 적용하기로 했음을 시사했습니다.', 16),
(33, 'Low', '서울 마포구에서 360만 원을 3년간 모으면 1440만 원과 이자를 받을 수 있는 청년내일저축계좌 프로그램이 시작됩니다. 이 프로그램은 저소득 청년들이 경제적으로 독립할 수 있도록 지원하며, 19세에서 34세의 청년들이 매달 10만 원의 지원금을 받을 수 있습니다. 기초생활수급자와 차상위계층은 정부지원금이 월 30만 원으로 늘어나며, 만기 시 1440만 원을 받을 수 있습니다. 이 프로그램은 박강수 마포구청장의 지원 아래 시행되며, 궁금한 점은 마포구 실뿌리복지과에 문의하시면 됩니다.', 20),
(34, 'High', '하워드 러트닉 미국 상무장관은 기본 10% 관세를 유지하지만 국가별로 협상해 철강, 알루미늄, 항공기 부품은 무관세로 수입될 수 있다 밝혔습니다. 지난 8일 영국과의 협상에서는 영국산 철강·알루미늄에 대한 25% 관세를 폐지하고, 보잉사와 롤스로이스사의 항공기 부품 관련 조건을 합의했습니다. 러트닉 장관은 이러한 사례가 미국의 유연한 대응을 보여준다고 강조했습니다.', 16),
(35, 'High', '[이데일리 홍수현 기자] 19-34세 청년 대상 청년내일저축계좌 시행. 저소득 청년 경제 자립 지원 목적으로, 매월 저축액 최대 3배 정부지원금 제공. 기준 중위소득 50~100% 해당 청년 대상, 3년 만기 360만 원 저축 시 720만 원 적립금과 적금이자 수령 가능. 기초생활수급자, 차상위계층은 15세부터 참여 가능하며, 지원금 월 30만 원으로 증액. 3년간 근로활동 및 자립 역량 교육 10시간 이수 필요. 박강수 마포구청장은 사업이 청년 경제적 도움 될 것 기대. 신청은 복지로 누리집, 주소지 시군구 동주민센터에서 가능.', 20),
(36, 'High', '한국철도공사(코레일)는 2025년도에 전국 기차역 노숙인 104명을 위해 자립 지원 사업을 전개합니다. 서울 18명, 영등표 16명, 청량리, 수원, 대전 각각 10명, 의정부, 천안 각각 8명, 모란, 대구, 부산, 구포 각각 6명이 대상입니다. 이들은 6개월 동안 역 광장 환경미화 및 관련 업무에 참여하며, 월 60시간 근무해 급여를 받습니다. 코레일은 일거리와 비용을, 지자체는 주거비와 자활 프로그램, 노숙인지원센터는 근로자 선발과 운영을 담당합니다. 그동안 802개의 일자리를 창출하고 1247명에게 자립 기회를 제공했습니다.', 17),
(37, 'High', '한솔그룹의 한솔홀딩스가 한솔피엔에스 잔여주식 15.86%를 공개매수하여 상장폐지 추진. 이전 1차 공개매수 진행하여 84.14% 확보. 이번 2차 공개매수는 5월 12일부터 6월 22일까지 진행, 매수가는 1주당 1900원. NH투자증권이 담당. 한솔그룹은 삼성그룹에서 분리된 중견그룹. 한솔피엔에스는 IT서비스업 및 지류유통 사업 확대 중.', 18),
(38, 'High', '한국은 1분기 -0.2% 성장하며 19개국 중 최하위였습니다. 탄핵정국 등의 정치 혼란과 미국 관세 정책으로 경제가 뒷걸음질 쳤고, 한국은행은 기준금리 인하를 고려 중입니다.', 19),
(39, 'Low', '우리나라는 1분기 -0.2% 성장률로 19개국 중 꼴찌입니다. 정치적 혼란과 미국 불확실성 등이 원인입니다. 우리 경제는 장기간 뒷걸음질 중이며, 정부와 한국은행이 금리 인하와 같은 대책이 필요합니다. 6월 대선 후 새 정부에서 추가 예산이 불가피할 수 있습니다.', 19),
(40, 'Low', '한솔그룹의 한솔홀딩스는 자회사 한솔피엔에스의 주식 15.86%를 공개매수해 상장폐지를 추진합니다. 3월-4월 1차 매수에 이어 진행되며, 1주당 가격은 1900원입니다. 한솔홀딩스는 이번 매수로 지분을 100% 확보합니다. 한솔피엔에스는 시스템통합과 유통사업을 하는 계열사며, 한솔그룹은 삼성그룹에서 분리된 중견그룹으로 많은 상장사를 보유 중입니다. 한솔피엔에스 상장폐지를 위해서는 95% 지분 확보가 필요합니다.', 18),
(41, 'High', '미국이 영국과 무역 협상 타결 후 중국과도 제네바에서 회담을 가졌습니다. 미국과 중국은 고위급 회담에서 관세 인하에 대해 논의했습니다. 트럼프 대통령은 무역 관계 재조정 논의가 진행 중이라며 진전을 강조했습니다. 영국과의 협상에서 미국산 소고기 관세가 없어지고, 미국은 영국산 자동차 관세를 인하하기로 합의했습니다. 한국과도 협상이 진행 중이며, 일본은 협상 시점을 조율 중입니다. 관세 협상은 미국 물가와 글로벌 경제에 중요한 영향을 미치고 있습니다.', 26),
(42, 'Low', '미국은 영국과의 협상 타결에 이어, 중국과도 협상 속도를 높이고 있다. 스콧 베선트 미국 재무부 장관과 허리펑 중국 국무원 부총리는 제네바에서 관세 인하를 위한 논의를 진행했다. 트럼프 대통령은 미중 무역 관계의 재조정 논의가 진전되었음을 밝혔다. 미국은 영국과의 협상에서 자동차 관세를 인하하고, 중국과도 관세율을 낮추기 위한 조기 타결을 추진 중이다.', 26),
(43, 'High', '러시아 모스크바 붉은광장에서 열린 전승절 80주년 열병식에서 러시아군이 참가했습니다. 우크라이나 전장에 지상군을 파병한 북한도 고위급 대표단을 파견했습니다. 행사에 참석한 시진핑 중국 국가주석은 블라디미르 푸틴 러시아 대통령과 대화를 나누었고, 두 정상은 여러 협력 문서에 서명했습니다.', 22),
(44, 'Low', '베트남에서 음식 관련 문제들이 발생하고 있습니다. 숙주로 많이 쓰이는 재료에 약품을 사용해 크게 만드는 ''벌크업'' 현상이 문제입니다. 숙주를 무해하게 만든다고 했지만, 사실은 건강에 해로운 성분이 사용됐습니다. 닥락성에서 35세 여성이 이를 먹고 병원에 가 사망하기도 했습니다. 두리안 과일에도 불법 약물이 사용되어 문제가 됐습니다. 건강 식품들도 가짜 제품으로 밝혀져 논란이 생겼습니다. 이렇게 많은 가짜 제품은 베트남에서 문제가 되고 있으며, 정부도 이에 대한 조치를 강화하고 있습니다.', 25),
(45, 'Low', '체코에서 프랑스 전력회사인 EDF가 소송을 제기하여 한국의 원자력발전소 계약이 중단되었습니다. 이에 한국과 체코는 손해를 봤고, 계약이 더 늦어질 수 있습니다. 한국 정부는 프랑스의 방해에 대해 인지하고 있었지만 대비가 부족했습니다. 체코에 방문한 국회의원들은 한국의 강한 의지를 보여주었습니다.', 24),
(46, 'High', '부석사 금동관세음보살좌상이 일본으로 반환되면서 일본의 문화재 반환 책임이 강조된다. 2012년 절도사건이 해결되며, 대법원도 불상이 약탈된 정황을 인정했다. 이전된 불상에 대한 일본의 적절한 후속조치가 기대되며, 이는 국제적 반환 추세에도 부합한다. 프랑스와 독일 등의 사례는 반환의 필요성을 보여준다. 이상근 문화유산회복재단 이사장은 일본의 책임있는 대응을 촉구했다.', 23),
(47, 'High', '체코에서 한국의 원전 수출 계약이 체코 법원의 결정으로 중단되었습니다. 한국수력원자력(한수원)이 체코의 원전 수주 경쟁에서 프랑스 전력공사(EDF)에 승리했지만, EDF는 계약 절차가 불공정했다며 소송을 제기해 계약이 지연되고 있습니다. 체코전력공사의 다니엘 베네시 CEO는 EDF가 유럽 시장에서 기득권을 놓치지 않기 위해 소란을 일으키고 있다고 말했습니다. 정부는 이런 법적 문제를 인식했지만, EDF의 이의 제기가 기각된 것을 믿고 방심한 부분이 있습니다. 체코에서 만난 한국 국회의원들과 안덕근 산업통상자원부 장관은 한국의 원전 완공에 확신을 보이고 있습니다.', 24),
(48, 'Low', '부석사 금동관세음보살좌상이 일본으로 반환됨에 따라 약탈 문화재 반환에 대한 일본의 책임 있는 대응 요구가 제기된다. 2012년 절도사건은 이번 반환으로 마무리되며, 대법원은 왜구에 의한 약탈 정황을 인정했다. 국제적으로도 약탈 문화재 반환이 늘고 있다. 박양진 충남대 교수는 유네스코 협약 이후 약탈된 문화유산을 반환하는 게 국제적 흐름이라고 설명했다. 이상근 문화유산회복재단 이사장은 일본이 약탈문화재에 대한 답을 해야 한다고 주장했다.', 23),
(49, 'Low', '러시아 모스크바에서 전승절 80주년 열병식이 열렸다. 러시아군이 행진하며 소리를 냈고, 북한은 고위급 대표단을 보냈다. 중국 시진핑 주석은 푸틴 대통령과 통역과 대화했고, 두 정상은 전날 회담 후 협력 문서에 서명했다.', 22),
(50, 'High', '베트남의 식품 안전 문제가 최근 주목받고 있습니다. 숙주나물의 "벌크업"이 화두인데, 이는 숙주나물을 화학 약품에 담가 키우는 것을 말합니다. 6-벤질아미노퓨린 약품이 사용되어 논란이 되었으며, 이는 베트남 전역에서 판매되었습니다. 또 두리안에도 불법 약물이 사용되었습니다. 두리안 성숙 가속 약품이 밀수되었습니다. 두리안 가격은 최근 하락했으며, 주요 수출 시장은 중국입니다. 기능성 분유와 다른 건강 제품들도 가짜로 밝혀졌습니다. 베트남 정부도 가짜 식품 단속에 착수했습니다. 이러한 상황은 빠른 경제 성장으로 유사 사례가 많았던 중국을 연상시킵니다.', 25);
